// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract DiamondGovernance is AccessControl {
    bytes32 public constant UPGRADE_ADMIN_ROLE =
        keccak256("UPGRADE_ADMIN_ROLE");

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(UPGRADE_ADMIN_ROLE, msg.sender);
    }

    modifier onlyUpgradeAdmin() {
        require(
            hasRole(UPGRADE_ADMIN_ROLE, msg.sender),
            "DiamondGovernance: must have upgrade admin role to interact"
        );
        _;
    }

    function addUpgradeAdmin(
        address newAdmin
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(UPGRADE_ADMIN_ROLE, newAdmin);
    }

    function removeUpgradeAdmin(
        address admin
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        revokeRole(UPGRADE_ADMIN_ROLE, admin);
    }
}

contracts/facets/DCAFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../interfaces/IDCA.sol"; // ! TODO: later add this interface.
import "hardhat/console.sol";
import "../interfaces/IUniswapV2Router.sol";

interface IAggregatorV3 {
    function getLatestPrice(address token) external view returns (int);
}

contract DCAFacet is ReentrancyGuard {
    using SafeERC20 for IERC20;
    struct DCAOrder {
        address fromToken;
        address toToken;
        uint256 totalAmount;
        uint256 amountPerInterval;
        uint256 interval;
        uint256 nextExecutionTime;
        uint256 orderCount;
        uint256 ordersPlaced;
    }
    struct OrderDetail {
        uint256 id;
        DCAOrder order;
        uint256 remainingAmount;
        uint256 currentValue;
        int currentPrice;
    }

    address public immutable priceAggregator;
    address public immutable uniswapRouter;
    uint256 public constant FEE_DECIMALS = 10000; // Represents 100.00%
    uint256 public feeRate = 250; // 2.5%

    event AllOrdersExecuted(address indexed user, uint256 index);
    event DCASetup(
        address indexed user,
        address fromToken,
        address toToken,
        uint256 amount,
        uint256 interval,
        uint256 orderCount
    );
    event DCAExecuted(
        address indexed user,
        address fromToken,
        address toToken,
        uint256 amount
    );

    mapping(address => DCAOrder[]) public userOrders;

    constructor(address _priceAggregator, address _uniswapRouter) {
        priceAggregator = _priceAggregator;
        uniswapRouter = _uniswapRouter;
    }

    // TODO: set modifier or make it ownable to only by governer.
    function setFees(uint256 _rate) external {
        require(_rate > 0 && _rate <= FEE_DECIMALS, "Fees must be 0 >= 100%");

        feeRate = _rate;
    }

    function setupDCA(
        address _fromToken,
        address _toToken,
        uint256 _totalAmount,
        uint256 _interval,
        uint256 _orderCount
    ) external {
        require(_interval > 0, "Interval must be > 0");
        require(_orderCount > 0, "Order count must be > 0");
        require(_totalAmount >= _orderCount, "Total amount too low");

        IERC20(_fromToken).safeTransferFrom(
            msg.sender,
            address(this),
            _totalAmount
        );

        console.log(
            "DCA::Balance After Transfer",
            IERC20(_fromToken).balanceOf(address(this))
        );

        uint256 feeAmount = (_totalAmount * feeRate) / FEE_DECIMALS;

        uint256 amountAfterFees = (_totalAmount - feeAmount);

        // ! convert fees to native currency so, contract can itself call some functions later.

        uint256 amountPerOrder = amountAfterFees / _orderCount;
        userOrders[msg.sender].push(
            DCAOrder({
                fromToken: _fromToken,
                toToken: _toToken,
                totalAmount: amountAfterFees,
                amountPerInterval: amountPerOrder,
                interval: _interval,
                nextExecutionTime: block.timestamp + _interval,
                orderCount: _orderCount,
                ordersPlaced: 0
            })
        );
        emit DCASetup(
            msg.sender,
            _fromToken,
            _toToken,
            _totalAmount,
            _interval,
            _orderCount
        );
    }

    function getOrderDetails(
        address user,
        uint index
    ) public view returns (OrderDetail memory) {
        require(index < userOrders[user].length, "Order index out of bounds");
        DCAOrder storage order = userOrders[user][index];
        uint256 remainingOrders = order.orderCount - order.ordersPlaced;
        uint256 remainingAmount = remainingOrders * order.amountPerInterval;

        // ! TODO: #pending: will need to provide it with oracle contracts first.
        // int price = IAggregatorV3(priceAggregator).getLatestPrice(
        //     order.tokenAddress
        // );

        // uint256 currentValue = (remainingAmount * uint256(price)) / 1e18;

        return
            OrderDetail({
                id: index,
                order: order,
                remainingAmount: remainingAmount,
                currentValue: 1e16,
                currentPrice: 0
            });
    }

    function getAllOrders(
        address user
    ) public view returns (DCAOrder[] memory) {
        return userOrders[user];
    }

    function getAllActiveOrders(
        address user
    ) public view returns (OrderDetail[] memory) {
        uint activeCount = 0;
        for (uint i = 0; i < userOrders[user].length; i++) {
            if (
                userOrders[user][i].ordersPlaced <
                userOrders[user][i].orderCount
            ) {
                activeCount++;
            }
        }

        OrderDetail[] memory activeOrders = new OrderDetail[](activeCount);
        uint j = 0;
        for (uint i = 0; i < userOrders[user].length; i++) {
            if (
                userOrders[user][i].ordersPlaced <
                userOrders[user][i].orderCount
            ) {
                activeOrders[j] = getOrderDetails(user, i);
                j++;
            }
        }

        return activeOrders;
    }

    function executeDCA(uint index) public {
        DCAOrder storage order = userOrders[msg.sender][index];
        require(
            block.timestamp >= order.nextExecutionTime,
            "Too early to execute"
        );
        require(
            order.ordersPlaced < order.orderCount,
            "Order already completed"
        );
        console.log("Executing DCA for user %s", msg.sender, address(this));

        address[] memory path = new address[](2);
        path[0] = order.fromToken;
        path[1] = order.toToken;

        // assuming, contract will always have enough balance to swap
        IERC20(order.fromToken).approve(uniswapRouter, order.amountPerInterval);

        IUniswapV2Router(uniswapRouter).swapExactTokensForTokens(
            order.amountPerInterval,
            0, // ! TODO: may use oracle to avoid impermanent loss
            path,
            msg.sender,
            block.timestamp
        );

        order.ordersPlaced++;
        order.nextExecutionTime += order.interval;

        emit DCAExecuted(
            msg.sender,
            order.fromToken,
            order.toToken,
            order.amountPerInterval
        );

        // !TODO: #pending check if all orders are placed and clean up or reset the order
        if (order.ordersPlaced == order.orderCount) {
            emit AllOrdersExecuted(msg.sender, index);
            // ....
        }
    }

    function checkUpkeep(
        bytes calldata /* checkData */
    )
        external
        view
        returns (bool upkeepNeeded, bytes memory /* performData */)
    {
        upkeepNeeded = false;
        for (uint256 i = 0; i < userOrders[msg.sender].length; i++) {
            if (
                block.timestamp >= userOrders[msg.sender][i].nextExecutionTime
            ) {
                upkeepNeeded = true;
                break;
            }
        }
        return (upkeepNeeded, bytes(""));
    }

    function performUpkeep(
        bytes calldata /* performData */
    ) external nonReentrant {
        for (uint256 i = 0; i < userOrders[msg.sender].length; i++) {
            if (
                block.timestamp >= userOrders[msg.sender][i].nextExecutionTime
            ) {
                executeDCA(i);
            }
        }
    }
}

contracts/facets/MultiBatchSwapFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../interfaces/IUniswapV2Router.sol";
import "../interfaces/IUniswapV2Factory.sol";
import "hardhat/console.sol";

contract MultiBatchSwapFacet is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    IUniswapV2Router public immutable uniswapRouter;
    IUniswapV2Factory public immutable uniswapFactory;

    uint256 public feeBasisPoints = 250; // 2.5%
    uint256 public constant BPS_DIVISOR = 10_000;

    constructor(address _uniswapRouter, address _uniswapFactory) {
        require(_uniswapRouter != address(0), "ERR::ZERO_ADDRESS::ROUTER");
        require(_uniswapFactory != address(0), "ERR::ZERO_ADDRESS::FACTORY");

        uniswapRouter = IUniswapV2Router(_uniswapRouter);
        uniswapFactory = IUniswapV2Factory(_uniswapFactory);
    }

    event TokensSwapped(
        address indexed user,
        address indexed fromToken,
        address indexed toToken,
        uint256 inputAmount,
        uint256 outputAmount,
        uint256 gasCost,
        uint256 slippage
    );

    event FeesCollected(address indexed collector, uint256 feeAmount);

    function batchSwapsToSingleToken(
        address[] memory inputTokens,
        uint256[] memory inputAmounts,
        address outputToken,
        address recipient,
        uint256 slippageTolerance
    ) external nonReentrant {
        require(
            inputTokens.length == inputAmounts.length,
            "Input lengths mismatch"
        );

        for (uint256 i = 0; i < inputTokens.length; i++) {
            console.log("Swapping token %s", inputTokens[i]);
            console.log("Amount %s", inputAmounts[i]);

            address inputToken = inputTokens[i];
            uint256 inputAmount = inputAmounts[i];

            IERC20(inputToken).safeTransferFrom(
                msg.sender,
                address(this),
                inputAmount
            );
            console.log("Transferred %s", inputAmount);

            uint256 feeAmount = (inputAmount * feeBasisPoints) / BPS_DIVISOR;
            uint256 swapAmount = inputAmount - feeAmount;

            console.log("Fee amount %s", feeAmount);
            console.log("Swap amount %s", swapAmount);

            _collectFees(inputToken, feeAmount);
            console.log("Fees collected");

            IERC20(inputToken).safeApprove(address(uniswapRouter), swapAmount);

            address[] memory path = new address[](2);
            path[0] = inputToken;
            path[1] = outputToken;

            console.log("Swapping %s to %s", inputToken, outputToken);
            uint256[] memory amountsOut = uniswapRouter
                .swapExactTokensForTokens(
                    swapAmount,
                    // (swapAmount * (BPS_DIVISOR - slippageTolerance)) /
                    //     BPS_DIVISOR, // May need to check it.
                    0,
                    path,
                    recipient,
                    block.timestamp
                );

            emit TokensSwapped(
                msg.sender,
                inputToken,
                outputToken,
                swapAmount,
                amountsOut[1],
                tx.gasprice,
                slippageTolerance
            );
        }
    }

    function batchSwapToSingleToken(
        address fromToken,
        address toToken,
        uint256 amount,
        address recipient
    ) external nonReentrant {
        IERC20(fromToken).safeApprove(address(uniswapRouter), amount);
        address _directPair = uniswapFactory.getPair(fromToken, toToken);
        address[] memory path;

        if (_directPair != address(0)) {
            // if direct pair exists
            path = new address[](2);
            path[0] = fromToken;
            path[1] = toToken;
        } else {
            path = determineSwapPath(fromToken, toToken);
        }

        uint256[] memory amountsOut = uniswapRouter.swapExactTokensForTokens(
            amount,
            0, // TODO: calculate or use Dex's function
            path,
            recipient,
            block.timestamp
        );

        emit TokensSwapped(
            msg.sender,
            fromToken,
            toToken,
            amount,
            amountsOut[amountsOut.length - 1],
            tx.gasprice,
            0 // for now
        );
    }

    function determineSwapPath(
        address fromToken,
        address toToken
    ) internal view returns (address[] memory) {
        address[] memory path = new address[](3);
        address intermediateToken = findIntermediateToken(fromToken, toToken);

        path[0] = fromToken;
        path[1] = intermediateToken;
        path[2] = toToken;

        return path;
    }

    function findIntermediateToken(
        address fromToken,
        address toToken
    ) internal pure returns (address) {
        return 0x3c499c542cEF5E3811e1192ce70d8cC03d5c3359; // USDC for now
    }

    function _collectFees(address token, uint256 feeAmount) internal {
        address feeCollector = owner();
        if (feeCollector != address(0)) {
            IERC20(token).safeTransfer(feeCollector, feeAmount);
            emit FeesCollected(feeCollector, feeAmount);
        }
    }

    function setFeeBasisPoints(uint256 newFeeBPS) external onlyOwner {
        require(newFeeBPS <= BPS_DIVISOR, "ERR::INVALID_FEE_BPS");

        feeBasisPoints = newFeeBPS;
    }

    function findBestSwapPath(
        address fromToken,
        address toToken
    ) internal view returns (address[] memory) {
        address directPair = uniswapFactory.getPair(fromToken, toToken);

        if (directPair != address(0)) {
            // Direct swap
            address[] memory directPath = new address[](2);
            directPath[0] = fromToken;
            directPath[1] = toToken;
            return directPath;
        } else {
            // Multi-hop
            address[] memory multiHopPath = new address[](3);
            multiHopPath[0] = fromToken;
            multiHopPath[1] = findIntermediateToken(fromToken, toToken);
            multiHopPath[2] = toToken;
            return multiHopPath;
        }
    }

    function estimateSwapOutput(
        address fromToken,
        address toToken,
        uint256 inputAmount
    ) public view returns (uint256[] memory) {
        address[] memory path = findBestSwapPath(fromToken, toToken);
        return uniswapRouter.getAmountsOut(inputAmount, path);
    }
}

